mapper.update:
update orders


        cancel_reason= #{cancelReason},


        status = #{cancelled},


        cancel_time = #{time}


where id in

    #{id}

mapper.select:
select *
from orders
where status = #{status}
  and order_time < #{localDateTime}

select * from orders


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select count(id) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}
